import { NextPageContext, NextComponentType } from 'next';

interface UserAgent {
    readonly source: string;
    readonly deviceType: string | null;
    readonly deviceVendor: string | null;
    readonly os: string;
    readonly osVersion: number;
    readonly browser: string;
    readonly browserVersion: number;
    readonly engine: string;
    readonly engineVersion: number;
    readonly isIphone: boolean;
    readonly isIpad: boolean;
    readonly isMobile: boolean;
    readonly isTablet: boolean;
    readonly isDesktop: boolean;
    readonly isBot: boolean;
    readonly isChrome: boolean;
    readonly isFirefox: boolean;
    readonly isSafari: boolean;
    readonly isIE: boolean;
    readonly isEdge: boolean;
    readonly isOpera: boolean;
    readonly isMac: boolean;
    readonly isChromeOS: boolean;
    readonly isWindows: boolean;
    readonly isIos: boolean;
    readonly isAndroid: boolean;
}

declare function useUserAgent(phase: string): UserAgent;

interface WithUserAgentProps extends JSX.IntrinsicAttributes {
    ua?: UserAgent;
}
interface WithUserAgentContext extends NextPageContext {
    ua?: UserAgent;
}
declare function withUserAgent<Props extends WithUserAgentProps, InitialProps>(ComposedComponent: NextComponentType<WithUserAgentContext, InitialProps, Props>): NextComponentType<WithUserAgentContext, InitialProps, Props>;

/**
 * Get the information of an useragent string.
 *
 * @param phrase user agent strings.
 * @returns parsed information.
 */
declare function parse(phrase: string): UserAgent;

export { UserAgent, WithUserAgentContext, WithUserAgentProps, parse, useUserAgent, withUserAgent };
